BEGIN
   declare p_trandate date;
   declare p_interval int(11);
   set p_trandate='2014-08-04';
   set p_interval=30;
   BEGIN
      DECLARE done, done_s int default 0;
      DECLARE vId bigint(20);
      DECLARE vPhone varchar(12);
      DECLARE vDatein, vDateend varchar(20);
      DECLARE c cursor FOR SELECT id, phone, datein, dateend FROM tmp_youtube_users WHERE b_usage = 0;
   
      SET net_write_timeout=12000;
      SET global connect_timeout=12000;
      SET net_read_timeout=12000;
      BEGIN
         declare continue handler for sqlstate '02000' set done = 1;
         OPEN c;
         REPEAT
            SET vId=null;
            SET vPhone=null;
            SET vDatein=null;
            SET vDateend=null;
            FETCH c INTO vId, vPhone, vDatein, vDateend;
            if not done then
               begin
                  declare continue handler for sqlstate '02000' set done_s = 1;
   
                  SELECT ifnull(sum(b_usage),'-1'), count(1)
                  INTO   @b_usage, @c_usage
                  FROM   tmp_youtube_usage
                  WHERE  phone = vPhone
                  and    start_tm between vDatein and vDateend;
   
                  UPDATE  tmp_youtube_users
                  SET     b_usage = @b_usage,
                          c_usage = @c_usage
                  WHERE   id=vId;
               end;
            end if;
         UNTIL done
         END REPEAT;
         close c;
      END;
   END;
   
   select count(1) into @total_subs from tmp_youtube_users where b_usage > 0;
   select p_trandate, grp_b, count(1) uniq, round((count(1)/@total_subs)*100,2) pct from (
   select phone, case 
                    when b_usage/1000000 > 50 then '50plus'
                    when b_usage/1000000 > 45 then '50mb'
                    when b_usage/1000000 > 40 then '45mb'
                    when b_usage/1000000 > 35 then '40mb'
                    when b_usage/1000000 > 30 then '35mb'
                    when b_usage/1000000 > 25 then '30mb'
                    when b_usage/1000000 > 20 then '25mb'
                    when b_usage/1000000 > 15 then '20mb'
                    when b_usage/1000000 > 10 then '15mb'
                    when b_usage/1000000 > 5  then '10mb'
                    when b_usage/1000000 > 0  then ' 5mb' 
                    end as grp_b
   from tmp_youtube_users where b_usage > 0) as t1
   group by 1,2 order by 1,2;

   select datein, phone, start_tm, end_tm, (b_usage/1000000) mb_used, c_usage from tmp_youtube_users where b_usage > 1000000 order by b_usage desc;
END ;;
